import React, { useState, useRef } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = 'http://localhost:5000';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);
  const [currentPlaylist, setCurrentPlaylist] = useState([]);
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const fileInputRef = useRef(null);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setError(null);
  };

  const handleAnalyze = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('file', selectedFile);

    setIsLoading(true);
    setError(null);
    setResults(null);

    try {
      const response = await axios.post(`${API_BASE_URL}/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.success) {
        setResults(response.data);
        if (response.data.parsed_playlist && response.data.parsed_playlist.length > 0) {
          setCurrentPlaylist(response.data.parsed_playlist);
          setCurrentSongIndex(0);
        }
      } else {
        setError(response.data.error || 'An error occurred while analyzing the image.');
      }
    } catch (err) {
      setError('Network error: ' + err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleRandomAnalyze = async () => {
    setIsLoading(true);
    setError(null);
    setResults(null);

    try {
      const response = await axios.post(`${API_BASE_URL}/analyze_random`);

      if (response.data.success) {
        setResults(response.data);
        if (response.data.parsed_playlist && response.data.parsed_playlist.length > 0) {
          setCurrentPlaylist(response.data.parsed_playlist);
          setCurrentSongIndex(0);
        }
      } else {
        setError(response.data.error || 'An error occurred while analyzing the random image.');
      }
    } catch (err) {
      setError('Network error: ' + err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      setSelectedFile(files[0]);
      if (fileInputRef.current) {
        fileInputRef.current.files = files;
      }
    }
  };

  const navigatePlaylist = (direction) => {
    if (currentPlaylist.length === 0) return;

    const newIndex = direction === 'next' ? currentSongIndex + 1 : currentSongIndex - 1;

    if (newIndex >= 0 && newIndex < currentPlaylist.length) {
      setCurrentSongIndex(newIndex);
    }
  };

  const openCurrentSongInSpotify = () => {
    if (currentPlaylist.length === 0) return;

    const song = currentPlaylist[currentSongIndex];
    window.open(song.spotify_url, '_blank');
  };

  const getCurrentSong = () => {
    if (currentPlaylist.length === 0) return null;
    return currentPlaylist[currentSongIndex];
  };

  return (
    <div className="App">
      <div className="container">
        <h1>🍽️ Food Ingredient & Playlist Analyzer</h1>
        <p className="subtitle">Upload a food image or analyze a random food photo to identify ingredients and get a matching playlist</p>

        <div className="upload-section">
          <div 
            className="file-input-wrapper"
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
          >
            <input 
              ref={fileInputRef}
              type="file" 
              id="fileInput" 
              className="file-input" 
              accept="image/*"
              onChange={handleFileChange}
            />
            <label 
              htmlFor="fileInput" 
              className={`file-input-button ${selectedFile ? 'has-file' : ''}`}
            >
              📸 {selectedFile ? `📁 ${selectedFile.name}` : 'Click to upload a food image or drag and drop'}
            </label>
          </div>
        </div>

        <button 
          className="analyze-button" 
          disabled={!selectedFile || isLoading}
          onClick={handleAnalyze}
        >
          🔍 Analyze My Image
        </button>

        <button 
          className="random-button" 
          disabled={isLoading}
          onClick={handleRandomAnalyze}
        >
          🎲 Analyze Random Food Image
        </button>

        {isLoading && (
          <div className="loading">
            <div className="spinner"></div>
            <p>Analyzing your food image and generating a playlist...</p>
          </div>
        )}

        {results && (
          <div className="results">
            <h3>📋 Identified Ingredients:</h3>
            <div className="ingredients">{results.ingredients}</div>

            <div className="playlist-title">
              🎵 Recommended Playlist:
            </div>
            <div className="playlist">{results.playlist}</div>

            <div className="source-info">
              Source: {results.source === 'uploaded_image' ? 'Your uploaded image' : 'Random food image from API'}
            </div>
          </div>
        )}

        {currentPlaylist.length > 0 && (
          <div className="spotify-player">
            <div className="current-song">
              <h4>{getCurrentSong()?.song || 'No song selected'}</h4>
              <p>{getCurrentSong()?.artist || 'Select a song to start playing'}</p>
            </div>

            <div className="player-controls">
              <button 
                className="player-button nav-button" 
                disabled={currentSongIndex === 0}
                onClick={() => navigatePlaylist('previous')}
              >
                ⏮️ Previous
              </button>
              <button 
                className="player-button spotify-button" 
                onClick={openCurrentSongInSpotify}
              >
                🎵 Open in Spotify
              </button>
              <button 
                className="player-button nav-button" 
                disabled={currentSongIndex >= currentPlaylist.length - 1}
                onClick={() => navigatePlaylist('next')}
              >
                Next ⏭️
              </button>
            </div>

            <div className="playlist-info">
              Song {currentSongIndex + 1} of {currentPlaylist.length}
            </div>
          </div>
        )}

        {error && (
          <div className="error">
            {error}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
